const assert = require('assert'); 
const script = require('../script.js');
const getRandomNumberInIntervalAsPromise = script.getRandomNumberInIntervalAsPromise;
const getRandomNumberInIntervalAsCallback = script.getRandomNumberInIntervalAsCallback;

/* Add test here */

describe('randomeNR', function (){
  it('promise', function() {
     return getRandomNumberInIntervalAsPromise(1, 10)
    .then(function(data) {
      assert.equal(typeof data, 'number');  // kolla om 'data' Ã¤r ett nummer
      assert.ok(data >= 1 && data <= 10);   //du kan ha villkoren inne i assertEn direkt
      console.log('VALUE OF PROMISE ' + data);
    });
  });
  it('callback',function(done) {
    getRandomNumberInIntervalAsCallback(5, 7, function(data) {
      let result = data >= 5 && data <= 7;  //Eller gÃ¶ra en variabel
      assert.ok(result);                    //och sen kalla pÃ¥ variablen vid assertEn
      console.log('VALUE OF CB ' + data);
      done();
    });
  });
});
