const assert = require('assert');
const Counter = require('../counter');

describe('Counter', function() {
  let counter = new Counter();
  // Write tests here
  afterEach(function() {
    counter.reset()
  });
  it('worsk to increase', function(){
    let count = counter.getCount();
    assert.equal(count, 0);
    counter.increase();
    let getNewCount = counter.getCount();
    assert.equal(getNewCount, 1);
    console.log('INCREASE ', getNewCount);
  });
  it('works with decrease', function() {
    let count = counter.getCount();
    assert.equal(count, 0);
    counter.decrease();
    let getNewCount = counter.getCount();
    assert.equal(getNewCount, -1);
    console.log('DECREASE ', getNewCount);
  });
});

